def readInput(f):
    with open(f) as fBuff:
        lines = fBuff.read()
    return lines

def getMarker(inText, uniqueLen):
    for i in range(len(inText)):
        unique = True
        chars = set()
        for j in range(i, i+uniqueLen):
            if(inText[j] in chars):
                unique = False
                break
            chars.add(inText[j])

        if(unique):
            return i+uniqueLen


